  const [isTrue, setIsTrue] = useState(false);
  const isLogin = (data) => {
    setIsTrue(prev =>{
      prev:!prev
    })

    // setIsTrue(data);
  };
  return (
    <div className="App">
      <Login isLogin = {isLogin}/>
     {isTrue && <Details />}
    </div>
  );
}

export default App;
