    const [post, setPost] = React.useState([]);
    const [changeValue, setchangeValue] = useState('red');
		const [value, setvalue] = useState(false)


    React.useEffect(() => {
      axios.get('https://jsonplaceholder.typicode.com/todos').then((response) => {
        setPost(response.data);

      });
    }, []);


    const funct = (id) => {


			console.log(id)
        setchangeValue('yellow')
				setvalue(true)
        console.log("hiii", changeValue)
    }
    return (
        <div>
            <h1>This has logined :) üëç</h1>
            <div className='data'>
                {post.map((item, index)=>
                    <div className='details' key={index}>
                        {/* {console.log(index)} */}
                        <p> <b>UserId = </b>{item.userId}</p>
                        <p> <b>Id = </b>{item.id}</p>
                        {item.completed?
                            <p> <b>Title =</b> <span  id='true'>{item.title}</span></p>:
                            <p> <b>Title =</b> <span onClick={item.completed === false ? () => funct(item.id):null} style={{ color:changeValue, cursor:'pointer'}} >{item.title}</span></p>
                        }

                        {/* <p> <b>Title =</b> <span>{item.title}</span></p> */}
                        {/* <p> <b>Completed = </b><button className='bool-value'>{item.completed}</button></p> */}
                    </div>)
                }
            </div>
        </div>
    )
}
